import echarts from 'echarts'
// 组件化开发 把一个项目分成若干个组件       类似于我们之前的公共样式和公共js
export default {
  // 小名 不能和 html 的标签名一样 一般都是小写的，一般是必须的
  name: 'app',
  data() {
    return {
      data: [],
      code: '01D726610125',
      analogType: '135',
      differ: '',
      value1: '',
      value2: ''
    }
  },
  mounted() {
    this.myChart = echarts.init(document.getElementById('main'))
    this.login()
  },
  methods: {
    open() {
      const h = this.$createElement

      this.$notify({
        title: '错误信息',
        message: h('i', { style: 'color: teal' }, '请填写正确信息')
      })
    },
    login(data) {
      this.$axios
        .get(
          'http://192.168.0.100:5400/openapi/AnalogHistory/QueryNbAnalogType',
          {
            params: {
              code: '01D726610125'
            }
          }
        )
        .then(res => {
          console.log(res.data.result[0])
        })
      this.$axios
        .get(
          'http://192.168.0.100:5400/openapi/AnalogHistory/QueryNbHistoryAnalog',
          {
            params: {
              code: this.code,
              analogType: this.analogType,
              differ: this.differ,
              start: this.value1,
              end: this.value2
            }
          }
        )
        .then(res => {
          var option = {
            xAxis: {
              type: 'category'
              //data: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun']
            },
            yAxis: {
              type: 'value'
            },
            series: [
              {
                data: res.data.result[0].values,
                type: 'line',
                smooth: true
              }
            ]
          }

          // 使用刚指定的配置项和数据显示图表。
          this.myChart.setOption(option)
        })
        .catch(function(error) {
          // this.$options.methods.open()
          console.log(error)
        })
    }
  },
  components: {}
}
